import React, { Component } from "react";
import CalcBlock from "./components/CalcBlock";
import ChooseOperation from "./components/ChooseOperation";
import CaclOut from "./components/CalcOut";
import ConverterOut from "./components/ConverterOut";
import History from "./components/History";

class App extends React.Component {

  constructor(props) {
    super(props);
    this.operation = "Calculator";
    this.convstate = "D-B";
    this.state = ({
      firstOp: "",
      secondOp: "",
      op: "",
      result: "",
      convop: "",
      convresult: "",
      history: ""
    });

    this.butMap = new Map();
    this.butMap.set("divide", "/");
    this.butMap.set("pow", "^");
    this.butMap.set("plus", "+");
    this.butMap.set("minus", "-");
    this.butMap.set("multiple", "*");

    this.setOperation = this.setOperation.bind(this);
    this.Click = this.Click.bind(this);
  }

  setOperation(e) {
    this.operation = e.target.value;
    console.log(this.operation);
  }

  converter(e) {

  }

  setOut(e) {
    if (this.state.op === "") {
      this.setState({
        firstOp: this.state.firstOp + e.target.id,
      });
    }
    else {
      this.setState({
        secondOp: this.state.secondOp + e.target.id,
      });
    }
  }

  erase() {
    this.setState({
      firstOp: "",
      secondOp: "",
      op: "",
      result: "",
      convop: "",
      convresult: "",
      history: ""
    });
  }

  calculate() {
    var result = NaN;
    var Error = false;
    if (op == "+") {
      result = parseInt(this.state.firstOp) + parseInt(this.state.secondOp);
    }
    else if (op == "-") {
      this.result = first - second;
    }
    else if (op == "/") {
      if (second != 0) {
        this.result = first / second;
      }
      else {
        this.ERROR = true;
      }
    }
    else if (op == "*") {
      this.result = first * second;
    }
    else if (op == "^") {
      this.result = Math.pow(first, second);
    }
    if (isNaN(this.result) || op == "") {
      this.ERROR = true;
      this.history = "ERROR\n";
    }
    else {
      this.history = first.toString() + op + second.toString() + "=" + this.result.toString() + "\n";
    }
  }
  Click(e) {
    if (this.operation === "Converter") {
      this.converter(e);
      return;
    }
    if (e.target.id === "res") {

      return;
    }
    if (e.target.id === "Erase") {
      this.erase();
      return;
    }
    for (var [key, value] of this.butMap) {
      if (e.target.id === key) {
        this.setState({
          op: value
        });
        return;
      }
    }
    this.setOut(e);
  }

  render() {

    return (
      <div className="container-fluid">
        <div className="text-center">
          <h1>My calculator</h1>
        </div>
        <ChooseOperation change={this.setOperation}></ChooseOperation>
        <div className="row">
          <div className="col-sm-5">
            <CalcBlock click={this.Click}></CalcBlock>
          </div>
          <div className="col-sm-7">
            <CaclOut
              first={this.state.firstOp}
              second={this.state.secondOp}
              op={this.state.op}
              result={this.state.result} />
            <ConverterOut operand={this.state.convop} result={this.state.convresult} />
          </div>
          <div className="col-sm-12">
            <History></History>
          </div>
        </div>
      </div>
    );
  }
}
export default App;
